  assign tl_s1n_2_ds_d2h[0] = tl_mbx_jtag__soc_i;

  assign tl_s1n_2_us_h2d = tl_dbg_i;
  assign tl_dbg_o = tl_s1n_2_us_d2h;

  always_comb begin
    // default steering to generate error response if address is not within the range
