    .tl_h_o       (tl_s1n_2_us_d2h),
    .tl_d_o       (tl_s1n_2_ds_h2d),
    .tl_d_i       (tl_s1n_2_ds_d2h),
    .dev_select_i (dev_sel_s1n_2)
